// src/backWorker.ts
self.addEventListener("message", async ({ data }) => {
  if (data.op in adapter.ops) {
    try {
      const res = await adapter.ops[data.op](...data.args);
      self.postMessage({
        reqID: data.reqID,
        status: "ok",
        result: res
      });
    } catch (err) {
      self.postMessage({
        reqID: data.reqID,
        status: "err",
        result: err
      });
    }
  } else {
    self.postMessage({
      status: "err",
      reqID: data.reqID,
      result: "method in service not exist"
    });
  }
});
var adapter = {
  ops: {},
  register(name, fn) {
    this.ops[name] = fn;
  }
};
